import React, { useState, useEffect } from "react";

const Feed = () => {

    const [postData, setPostData] = useState(null);
    const endpoint = 'http://localhost:8080/posts';

    useEffect(()=> {
        const fetchPosts = async() => {
            try {
                setPostData(
                    await fetch(endpoint)
                    .then((response) => response.json())
                )
            } catch(error){
                console.log("Some Error occured when fetching posts")
            }
        };
        fetchPosts();
    },[]);


    return (
        <div>
            {postData ? (
                <div>
                    <Stack>
                        {postData.map((post) => (
                            <Item>
                                <p>{post.title}</p>

                                <p>By: {post.user_name}</p>
                                <p>Said: {post.content}</p>
                            </Item>
                        ))}
                    </Stack>
                </div>
            ) : (
                <p>Loading Posts...</p>
            )}
        </div>
    );
};

export default Feed;
