import React, { useState, useEffect } from "react";
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import Divider from '@mui/material/Divider';
import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Avatar from '@mui/material/Avatar';
import Typography from '@mui/material/Typography';


const Feed = () => {

    const [postData, setPostData] = useState(null);
    const endpoint = 'http://localhost:8080/posts';



    useEffect(()=> {
        const fetchPosts = async() => {
            try {
                setPostData(
                    await fetch(endpoint)
                    .then((response) => response.json())
                )
            } catch(error){
                console.log("Some Error occured when fetching posts")
            }
        };
        fetchPosts();
    },[]);

    const handleSubmit = (event) =>{
        fetch('http://localhost:8080/post',{
            method: 'POST',
            headers: {'Content-Type':'application/json'},
            body: {
                "title": "New Post",
                "userName": "Master Bobba",
                "content": "Hi There  <img onerror=alert(''Congratulations_You_Hacked_Yourself''); src=invalid-image/>"
            }
        });
    };

    return (
        <div>
            {postData ? (
                <div>
                    <form onSubmit={handleSubmit}>
                        <label htmlFor="message">New Post:</label>
                        <textarea id="newPost" name="newPost"/>

                        <button type="submit">Submit</button>
                    </form>
                    <List sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper' }}>
                    {postData.map((post) => (
                        <div>
                            <ListItem alignItems="flex-start">
                            <ListItemAvatar>
                                <Avatar alt={post.userName} src="/static/images/avatar/1.jpg" />
                            </ListItemAvatar>
                            <ListItemText
                                primary= {post.title} by
                                secondary={
                                    <React.Fragment>
                                    <Typography
                                        sx={{ display: 'inline' }}
                                        component="span"
                                        variant="body2"
                                        color="text.primary"
                                    >
                                        {/* {post.userName} */}
                                    </Typography>
                                    <div dangerouslySetInnerHTML={{"__html":post.content}}></div>
                                    </React.Fragment>
                                }
                                />
                            </ListItem>
                            <Divider variant="inset" component="li" />
                        </div>  
                    ))}
                </List>
            </div>
            ): (
                <div>
                    Loading content...
                </div>

            )}
        </div>
    );
};

export default Feed;
