import React, { useState, useEffect } from "react";

const Feed = () => {

    const [postData, setPostData] = useState(null);
    const endpoint = 'http://localhost:8080/posts';

    useEffect(()=> {
        const fetchPosts = async() => {
            try {
                setPostData(
                    await fetch(endpoint)
                    .then((response) => response.json())
                )
            } catch(error){
                console.log("Some Error occured when fetching posts")
            }
        };
        fetchPosts();
    },[]);


    return (
        <div>
            {postData ? (
                <div className="border: 1px solid #b9b9b9; margin-left: 10px; margin-right: 10px; margin-top: 10px;">
                    <div class="row">
                        <ul>
                            {postData.map((post) => (
                                <li>
                                    <p>{post.title}</p>

                                    <p>By: {post.user_name}</p>
                                    <p>Said: {post.content}</p>
                                </li>
                            ))}
                        </ul>
                    </div>
                </div>
            ) : (
                <p>Loading Posts...</p>
            )}
        </div>
    );
};

export default Feed;
